<?php

namespace ReparacionBundle\Repository;

/**
 * TecnicoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TecnicoRepository extends \Doctrine\ORM\EntityRepository
{

	public function findAjaxValue($term){
        $em = $this->getEntityManager();
        $term = str_replace(' ', '%', $term);
        $consul = $em->createQuery('SELECT z FROM ReparacionBundle:Tecnico z
                                        WHERE  z.paterno LIKE \'%'.$term.'%\'
                                        OR  z.nombre LIKE \'%'.$term.'%\'');
        $resp = $consul->getResult();
        return $resp;
    }
}
